import "../css/home.css";
import "../css/leftSide.css";
import "../css/rightSide.css";
import React, { useState } from "react";

function Home() {
  var areaCovered = "Area 6";

  var editToggle = "Edit";
  var viewingEditingToggle = "Viewing";
  var saveButton = "Saved";
  const [editingMode, setEditingMode] = useState(false);
  const [savePreviewButton, setSaveButton] = useState("Saved");

  function toggleEdit() {
    if (editingMode == true) {
      setSaveButton("Save");
      setEditingMode(false);
    } else {
      setEditingMode(true);
    }
  }

  return (
    <div class="toolContainer">
      <div class="leftSide">
        <div class="leftHeader">
          <h2>Circuits for {areaCovered}</h2>
        </div>
        <div class="leftBody">
          <div class="spreadsheet"></div>
        </div>
        <div class="leftFooter">
          <h3 onClick={toggleEdit}>{editingMode ? "Edit" : "View"}</h3>
          <h4>- {editingMode ? "Viewing" : "Editing"} -</h4>
          <h3>{savePreviewButton}</h3>
        </div>
      </div>
      <div class="rightSide"></div>
    </div>
  );
}

export default Home;
