1;4.991;5.265;50;0.88;0.25;D;D;0.962;89.99;-;C;C
2.8;6.495;6.495;20;0.88;0.25;D;D;0.962;89.99;-;C;C
2.94;6.501;6.502;22.7;0.88;0.25;D;D;0.962;89.99;-;C;C
2.94;6.502;6.503;23.5;0.88;0.25;D;D;0.962;89.99;-;C;C
2.94;6.503;6.503;24.3;0.88;0.25;D;D;0.962;89.99;-;C;C
2.94;6.503;6.504;25;0.88;0.25;D;D;0.962;89.99;-;C;C
2.93;6.504;6.505;25.8;0.88;0.25;D;D;0.962;89.99;-;C;C
2.94;6.505;6.5;26.5;0.88;0.25;D;D;0.962;89.99;-;C;C
2.95;6.5;6.5;28.5;0.88;0.25;D;D;0.962;89.99;-;C;C
0.14;6.5;6.5;28.5;0.88;0.25;D;D;0.962;89.99;-;C;C
0.14;6.5;6.5;28.5;0.88;0.25;None;None;0.962;89.99;-;C;C
2.63;6.5;6.549;28.5;0.88;0.25;D;D;0.962;89.99;-;C;C
2.62;6.549;6.598;27.9;0.88;0.25;D;D;0.962;89.99;-;C;C
2.85;7;6.999;34;0.88;0.25;D;D;0.962;89.99;-;C;C
`;
export default function Sheet() {
  const jRef = useRef(null);

  const [formData, setFormData] = useState("");

  const options = {
    // data: [[]],
    data: CSV.parse(csv),
    minDimensions: [10, 10],
    // defaultColWidth: 100,
    tableOverflow: true,
    tableWidth: "100%",
    tableHeight: "400px",
    loadingSpin: true,
    // editable: false
  };

  useEffect(() => {
    if (!jRef.current.jspreadsheet) {
      jspreadsheet(jRef.current, options);
    }
  }, []);

  const addRow = () => {
    jRef.current.jexcel.insertRow();
  };

  const convertToCSV = () => {
    const rows = jRef.current?.jexcel.getData();

    setFormData(CSV.stringify(rows, ";"));
  };

  return (
    <div>
      <div ref={jRef} />
      <br />
      <input type="button" onClick={addRow} value="Add new row" />

      <button onClick={() => jRef.current.jexcel.selectAll()}>
        Select All
      </button>

      <button onClick={() => console.log(jRef.current?.jexcel.getData())}>
        Get all data
      </button>

      <button onClick={convertToCSV}>Convert to CSV</button>

      <pre>{formData}</pre>
    </div>
  );
}
